    --jumpback-icon: url('data:image/svg+xml;charset=utf-8;base64,');
    --cc-icon: url('data:image/svg+xml;charset=utf-8;base64,');
    --cc-icon-selected: url('data:image/svg+xml;charset=utf-8;base64,');
    --muted-icon: url('data:image/png;base64,');
    --muted-icon-selected: url('data:image/png;base64,');
    --volume-icon: url('data:image/png;base64,');
    --volume-icon-selected: url('data:image/png;base64,');
    --fullscreen-icon: url('data:image/png;base64,');
    --fullscreen-icon-selected: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAACAAAAAgCAYAAABzenr0AAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH4wELDQoevl0rhwAAAGBJREFUWMPtlUsKQCEMA83D+1857h/WH0JdTJeiMrYxke2SWV9JLgAAACAdoPYWJU3dybZunHuzA6NXnu6PuoMI0wFEHAMQ/dkVV9sSW3AnI3g7C0Y6OE1DnBARAgDAvxoYrCFD1ewiTwAAAABJRU5ErkJggg==');
